import { createAction } from "redux-actions";
import LoginHome from "../LoginHome";
import Login from "../pages/Login";

const TRUELOGIN = 'login/TRUELOGIN';
//const FALSELOGIN = 'login/FALSELOGIN';

//export const truelogin = () => ({type: TRUELOGIN});
export const truelogin = createAction(TRUELOGIN);
// export const falselogin = () => ({type: FALSELOGIN});


const initialState = {
    link: <Login/>
};

function successlogin(state = initialState, action){
    switch (action.type){
        case TRUELOGIN:
            return{
                link: state.link = <LoginHome/>
            }
        default:
            return state;
    }
}

export default successlogin;
