import LoginHome from "../LoginHome";

const TRUELOGIN = 'login/TRUELOGIN';
//const FALSELOGIN = 'login/FALSELOGIN';

export const truelogin = () => ({type: TRUELOGIN});
// export const falselogin = () => ({type: FALSELOGIN});

const [Login,SetLogin] = useS
const initialState = {
    link: <Login/>
};

function success(state = initialState, action){
    switch (action.type){
        case TRUELOGIN:
            return{
                link: state.link = <LoginHome/>
            }
        default:
            return state;
    }
}

export default success;
