import { Link, useNavigate } from "react-router-dom";
import { RecoilRoot, useRecoilState } from "recoil";
import styled from "styled-components";
import './App.css';
import { tokenState } from "./GlobalState";
const Li=styled.li`
    list-style-type : none;
    margin-right:50px;
    flex-basis: 4.79%;
`;
const Img=styled.img`
    width:70px;
    height:50px;
    margin-top:35px;
    padding: 0;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    display: inline-block;
    vertical-align: middle;
`;
const Nav=styled.nav`
    vertical-align: middle;
`;
const Ul=styled.ul`
    list-style: none;
    display:grid;
    text-align:right;
`;
export default function Home(){
    const [token, setToken] = useRecoilState(tokenState);

    const logoutToken = () => {
        localStorage.removeItem('X-AUTH-TOKEN');
        setToken('');
    };    
    return(
        <div>
            <div className="header">
                <RecoilRoot>
                    <a href="/" className="position"><Img src="./dijindaPark.png" alt='디진다돈까스'/></a>
                    <Nav>
                    <Ul>
                        <Li>
                            <Link to='./timer' className="line">타이머</Link>
                        </Li>
                        <Li>   
                            <Link to='./rank' className="line">랭킹</Link>
                        </Li>
                        {token ? (
                            <Li><Link onClick={logoutToken} className="line">로그아웃</Link></Li>
                        ) : (
                            <>
                            <Li><Link to='./login' className="line">로그인</Link></Li>
                            <Li><Link to='./signup' className="line">회원가입</Link></Li>
                            </>
                        )}
                        
                    </Ul>
                    </Nav>
                </RecoilRoot>
            </div>
        </div>
    );
}
