import './App.css';
import {Route,Routes} from 'react-router-dom';
import Timer from './pages/Timer';
import Login from './pages/Login';
import Ranking from './pages/Ranking';
import Error from './pages/Error';
import Home from './Home';
import Layout from './Layout';
import LoginHome from './LoginHome';
import SignUp from './pages/SignUp';
import MyPage from './pages/MyPage';
import { useReducer,use } from 'react';
const initialState = {
  authenticated: false,
  token: null
}
function reducer(state, action){
  switch(action.type) {
      case 'SET_TOKEN':
          return {...state, token: action.token, authenticated: action.result};
      default:
          return state;
  }
}
function App() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const { authenticated } = state;

  function handleLogin(id, password) {
    let token = auth.login(id, password);

    if (token) {
      console.log('로그인 성공!');
      dispatch({
        type: 'SET_TOKEN',
        token: token,
        result: true,
      });
    } else {
      console.log('로그인 실패');
      dispatch({
        type: 'SET_TOKEN',
        token: null,
        result: false,
      });
    }
  }
  return (
    <div>
      <Routes>
        <Route path='/' element={<Layout/>}>
          <Route index element={<Home/>}/>
          <Route path='/timer' element={<Timer />}/>
          <Route path='/login' authenticated={authenticated} element={<Login {...props} handleLogin={handleLogin} />}/>
          <Route path='/SignUp' element={<SignUp />}/>
          <Route path='/rank' element={<Ranking />}/>
        </Route>
        <Route path='/' element={<Layout/>}>
          <Route index element={<LoginHome/>}/>
          <Route path='/timer' element={<Timer />}/>
          <Route path='/rank' element={<Ranking />}/>
          <Route path='/mypage' element={<MyPage/>}/>
        </Route>
        <Route path='*' element={<Error/>}/>
      </Routes> 
    </div>
  )
}

export default App;
