import { Link, useNavigate } from "react-router-dom";
import { RecoilRoot, useRecoilState } from "recoil";
import styled from "styled-components";
import './App.css';
import { tokenState } from "./GlobalState";
const Li=styled.li`
    list-style-type : none;
    margin-right:50px;
`;
const Img=styled.img`
    width:70px;
    height:50px;
    position: relative;
    top:45px;
    right:900px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
`;
const Ul=styled.ul`
    display:flex;
    margin-top:55px;
`;
export default function Home(){
    const navigate=useNavigate();
    const [token, setToken] = useRecoilState(tokenState);

    const logoutToken = () => {
        localStorage.removeItem('X-AUTH-TOKEN');
        setToken('');
    };    
    return(
        <div>
            <div className="header">
                <nav className="position">
                <RecoilRoot>
                    <a href="/"><Img src="./dijindaPark.png"/></a>
                    <Ul>
                        <Li>
                            <Link to='./timer' className="line" target='_blank'>타이머</Link>
                        </Li>
                        <Li>   
                            <Link to='./rank' className="line">랭킹</Link>
                        </Li>
                        <Li>
                            <Link to='./signup' className="line">회원가입</Link>
                        </Li>
                        {token ? (
                            <Li onClick={logoutToken}>로그아웃</Li>
                        ) : (
                            <Link to='./login' className="line">로그인</Link>
                        )}
                    </Ul>
                </RecoilRoot>
                </nav>
            </div>
        </div>
    );
}
