import styled from "styled-components";

const Bg=styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    background-repeat:no-repeat;
    background-size: 300% 300%;
    background-image: linear-gradient(
        -45deg, 
        #3be35f 0%, 
        rgba(87,111,230,1) 25%, 
        #57e1e6 51%, 
        #70e33b 100%
    );  
    animation: AnimateBG 20s ease infinite;

    @keyframes AnimateBG { 
        0%{background-position:0% 50%}
        50%{background-position:100% 50%}
        100%{background-position:0% 50%}
    }
`;

const WhiteBar=styled.div`
    width:650px;
    height:550px;
    margin:50px auto;
    background-color:white;
    border-radius:1.2rem;
    align-items: center;
    justify-items:center;
`;
const Title=styled.div`
    font-weight:bold;
    color:black;
    font-size:50px;
    position:relative;
    font-family: "work sans";
    left:245px;
`;
const IdPass=styled.div`
    margin:50px auto;
`;
const IdDesign=styled.input`
    Color:black;
    outline:none;
    border:none;
    position: relative;
    left:170px;
    margin:70px 0 15px 0;
    font-family: "work sans";
    width:300px;
    font-size:18px;
`;
const Border1=styled.div`
    border-bottom:3px solid gray;
    width:50%;
    position:relative;
    left:150px;
`;

const Border2=styled.div`
    border-bottom:3px solid gray;
    width:50%;
    position:relative;
    left:150px;
`;
const PasswordDesign=styled.input`
    font-family: "work sans";
    Color:black;
    outline:none;
    margin:35px 0 15px 0;
    border:none;
    position: relative;
    left:170px;
    width:300px;
    font-size:18px;
`;

const LoginBtn=styled.button`
    color
    font-family: "work sans";
`;
export default function Login(){
    return(
        <div>
            <Bg>
                <WhiteBar>
                    <IdPass>
                        <Title>로그인</Title>
                        <div><IdDesign type='text' placeholder="Enter your name"/></div>
                        <Border1/>
                        <div><PasswordDesign type='password' placeholder="Enter your password"/></div>
                        <Border2/>
                        <LoginBtn>로그인</LoginBtn>
                    </IdPass>
                </WhiteBar>
            </Bg>
        </div>
    )
}
